function hello_elementor_child_scripts_styles()
{

	wp_enqueue_style(
		'hello-elementor-child-style',
		get_stylesheet_directory_uri() . '/style.css',
		[
			'hello-elementor-theme-style',
		],
		HELLO_ELEMENTOR_CHILD_VERSION
	);

	wp_enqueue_style(
		'main-style',
		get_stylesheet_directory_uri() . '/assets/css/frontend.css',
		[],
		HELLO_ELEMENTOR_CHILD_VERSION
	);

	wp_enqueue_script(
		'main-script',
		get_stylesheet_directory_uri() . '/assets/js/frontend.js',
		array('jquery'),
		null,
		true
	);
}
add_action('wp_enqueue_scripts', 'hello_elementor_child_scripts_styles', 20);

function shortcode_member_grid_slider($atts)
{
	$atts = shortcode_atts(array(), $atts, 'member_grid_slider');

	ob_start();

	get_template_part(
		'templates/member-grid-slider',
		null,
		array()
	);

	return ob_get_clean();
}
add_shortcode('member_grid_slider', 'shortcode_member_grid_slider');

