function twmp_get_post_meta_items($allow_keys = [])
{
	$items = [];
	$post_date = get_the_date();

	if (!empty($post_date) && $allow_keys['date']) {
		$items['post_date'] = [
			'icon' => twmp_get_svg_icon('calendar'),
			'text' => $post_date
		];
	}

	$author_name = get_the_author_meta('user_nicename');

	if (!empty($author_name) && $allow_keys['author']) {
		$items['post_author'] = [
			'icon' => twmp_get_svg_icon('user'),
			'text' => $author_name
		];
	}

	if (get_post_type() === 'tin-tuc') {
		$categories = wp_get_post_terms(get_the_ID(), 'danh-muc');
	} else {
		$categories = get_the_category();
	}

	if (!empty($categories) && $allow_keys['categories']) {
		$category_html = '';
		// foreach ($categories as $index => $category) {
		// 	if ($index > 0) {
		// 		$category_html .= ', ';
		// 	}

		// 	$category_html .= sprintf(
		// 		'<a class="text-dark text-decoration-none category-item" href="%1$s" title="%2$s">%3$s</a>',
		// 		get_category_link($category),
		// 		esc_attr($category->name),
		// 		$category->name
		// 	);
		// }

		$category_html .= sprintf(
			'<a class="text-dark text-decoration-none category-item" href="%1$s" title="%2$s">%3$s</a>',
			get_category_link($categories['0']),
			esc_attr($categories['0']->name),
			$categories['0']->name
		);

		$items['post_category'] = [
			'icon' => twmp_get_svg_icon('folders'),
			'text' => $category_html
		];
	}

	return $items;
}

// Cho phép SVG upload
function allow_svg_upload($mimes)
{
	$mimes['svg'] = 'image/svg+xml';
	return $mimes;
}
add_filter('upload_mimes', 'allow_svg_upload');

// Bỏ kiểm tra lỗi MIME type SVG
function fix_svg_mime_type($data, $file, $filename, $mimes, $real_mime)
{
	$ext = strtolower(pathinfo($filename, PATHINFO_EXTENSION));
	if ($ext === 'svg') {
		$data['ext'] = 'svg';
		$data['type'] = 'image/svg+xml';
	}
	return $data;
}
add_filter('wp_check_filetype_and_ext', 'fix_svg_mime_type', 10, 5);

// Chặn hiển thị Checkout trong register form (form "create" hoặc "register")
add_filter('ump_before_submit_form', 'ihc_disable_checkout_on_register_form', 9999, 4);
function ihc_disable_checkout_on_register_form($output, $is_public, $typeOfForm, $register_level)
{
	// $is_public == true khi public form, $typeOfForm có thể 'create' (Register) hoặc 'register' (lite)
	if ($is_public === true && ($typeOfForm === 'create' || $typeOfForm === 'register')) {
		return ''; // xóa output, checkout sẽ không hiển thị
	}
	return $output;
}

