/**
 * Khi login qua IHC login form: nếu URL chứa `redirect_to` và trỏ tới trang checkout (cùng host),
 * thì cho phép redirect về `redirect_to` thay vì redirect mặc định của IHC.
 */
add_filter('wp_redirect', 'twmp_override_ihc_login_redirect_to_checkout', 10, 2);
function twmp_override_ihc_login_redirect_to_checkout($location, $status)
{
	global $wpdb;

	// Only handle when the IHC login form was submitted
	if (! empty($_REQUEST['ihcaction']) && sanitize_text_field($_REQUEST['ihcaction']) === 'login') {

		if (! empty($_REQUEST['redirect_to'])) {

			$raw = wp_unslash($_REQUEST['redirect_to']);
			$redirect_to = urldecode($raw);

			// Parse redirect_to to lấy userId
			$parsed = parse_url($redirect_to);
			parse_str($parsed['query'] ?? '', $params);

			if (! empty($params['userId'])) {

				echo '<pre>';
				print_r($params['userId']);
				echo '</pre>';
				$user_id = intval($params['userId']);

				// Query level_id
				$query = $wpdb->prepare("
					SELECT *
					FROM {$wpdb->prefix}ihc_user_levels
					WHERE user_id = %d
					ORDER BY start_time DESC
					LIMIT 1
				", $user_id);

				$data = $wpdb->get_row($query);

				// echo '<pre>';
				// print_r($data);
				// echo '</pre>';
				// die();
				if (! empty($data->level_id)) {

					// Build new redirect URL
					$final_url = site_url('/checkout-page/') . '?lid=' . intval($data->level_id);

					return esc_url_raw($final_url);
				}
			}
		}
	}

	return $location;
}

// Ensure `approve_account` notification templates still receive the full set of constants
add_filter('ihc_filter_constants_for_notifications', 'twmp_restore_constants_for_approve_account', 10, 2);
function twmp_restore_constants_for_approve_account($constants, $type)
{
	if ($type === 'approve_account') {
		$needed = array(
			'{username}',
			'{user_id}',
			'{user_email}',
			'{first_name}',
			'{last_name}',
			'{account_page}',
			'{login_page}',
			'{current_level}',
			'{current_level_expire_date}',
			'{level_list}',
			'{blogname}',
			'{blogurl}',
			'{currency}',
			'{amount}',
			'{level_name}',
			'{current_date}',
		);

		foreach ($needed as $key) {
			if (! isset($constants[$key])) {
				$constants[$key] = ''; // provide an empty default so templates can use the placeholder safely
			}
		}
	}

	return $constants;
}

add_action( 'template_redirect', 'twmp_redirect_default_search_to_newsletter' );
function twmp_redirect_default_search_to_newsletter() {

    // Chỉ thực thi khi đang ở trang search mặc định ?s=...
    if ( is_search() && isset($_GET['s']) ) {

        $search = sanitize_text_field($_GET['s']);

        // URL trang newsletter của bạn
        $target = home_url( '/vis-newsletters/' );

        // Redirect 301/302
        wp_redirect( add_query_arg( 'search_key', $search, $target ) );
        exit;
    }
}

add_filter('get_search_form', function($form){
    $form = str_replace(
        'action="' . esc_url(home_url('/')) . '"',
        'action="' . esc_url(home_url('/vis-newsletters/')) . '"',
        $form
    );
    return $form;
});